// Add event listener to state dropdown
document.getElementById('state').addEventListener('change', function() {
    const selectedState = this.value;
    const districtSelect = document.getElementById('district');

    // Clear previous district options
    districtSelect.innerHTML = '<option value="">Select District</option>';

    if (selectedState && stateDistrictMap[selectedState]) {
        // Populate the district dropdown with districts from the selected state
        stateDistrictMap[selectedState].forEach(function(district) {
            const option = document.createElement('option');
            option.value = district;
            option.textContent = district;
            districtSelect.appendChild(option);
        });
    }
});

